<script id=\"payment-source-template\" type=\"text/template\">
<p class=\"payment-from\">
    <div class=\"need-deposit payment-from-option\" style=\"display:none\">
        <%- T_('You will be directed to the deposit page to make this payment.') %>
    </div>
    <div class=\"may-need-deposit payment-from-option\" style=\"display:none\">
        <%- T_('This will be debited from your available account balances, then if required, you will be directed to the deposit page to make this payment:') %>
    </div>
    <div class='only-balance payment-from-option' style=\"display:none\">
        <%- T_('This will be debited from your available account balances:') %>
    </div>
    <% if (templateData.firstAvailablePaymentMethod) { %>
        <div class=\"only-vpm payment-from-option\" style=\"display:none\">
            <% if (templateData.verifiedPaymentSourceCount == 1) { %>
                <%- T_('This will be debited from your verified payment method:') %>
            <% } else { %>
                <%- T_('This will be debited from your verified payment methods:') %>
            <% } %>
        </div>
        <div class=\"mixed-balance-vpm payment-from-option\" style=\"display:none\">
            <% if (templateData.verifiedPaymentSourceCount == 1) { %>
                <%- T_('This will be debited from your available balances, then if required we will attempt to process this payment from your verified payment method:') %>
            <% } else { %>
                <%- T_('This will be debited from your available balances, then if required we will attempt to process this payment from your verified payment methods:') %>
            <% } %>
        </div>
    <% } %>
</p>
<span class=\"account-balances payment-source-balance\" style=\"display:none\">
    <strong><%- T_('Account Balance:') %></strong>
    <% if (templateData.balances) { %>
        <% for (var i = 0; i < templateData.balances.length; i++) { %>
            <%- templateData.balances[i].currency.sign %><%- templateData.balances[i].balance.toFixed(2) %> <%- templateData.balances[i].currency.code %><%- (i == templateData.balances.length - 1) ? '' : ',' %>
        <% } %>
    <% } %>
</span>
<% if (templateData.firstAvailablePaymentMethod) { %>
    <div class=\"payment-logos-container\" style=\"display:none\">
        <h6>
            <%- T_('Verified payment method:') %>
        </h6>
        <img src=\"<%= cdn_url('/img/ajax-loader.gif') %>\" class=\"loading-img-change-preferred-method \" style=\"display:none\" />
        <span class=\"payment-source-option\">
            <span class=\"payment-source-icons\">
                <span class=\"payment-logo-container paypal gatewayPP\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayPP') { %>
                        style=\"display:none\"
                    <% } %>>
                    <div id=\"pp-icon\">
                      <span class=\"payment-icon-paypal\"></span>
                    </div>
                </span>
                <span class=\"payment-logo-container cc gatewayGC gatewayWP\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayGC' && templateData.firstAvailablePaymentMethod != 'gatewayWP') { %>
                        style=\"display:none\"
                    <% } %>>
                    <div id=\"cc-icon\">
                        <span class=\"payment-icon-mastercard\"></span>
                        <span class=\"payment-icon-visa\"></span>
                        <span class=\"payment-icon-amex\"></span>
                    </div>
                </span>
                <span class=\"payment-logo-container skrill gatewayMB\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayMB') { %>
                        style=\"display:none\"
                    <% } %>>
                    <div id=\"mb-icon\">
                      <span class=\"payment-icon-skrill\"></span>
                    </div>
                </span>
            </span>
            <span class=\"payment-source-detail\">
                <span class=\"showing-method-account-details gatewayPP\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayPP') { %>
                        style=\"display:none\"
                    <% } %>>
                    <% if (templateData.paymentMethods.ppEmail) { %>
                        <%- templateData.paymentMethods.ppEmail %>
                    <% }%>
                </span>
                <span class=\"showing-method-account-details gatewayGC\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayGC') { %>
                        style=\"display:none\"
                    <% } %>>
                    <% if (templateData.paymentMethods.gcRecurring.length == 1) { %>
                        <span><%- templateData.paymentMethods.gcRecurring[0]['cc_number'] %></span>
                    <% } else { %>
                        <span class=\"payment-source-tooltip gatewayGC \"><%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.gcRecurring.length}) %></span>
                    <% } %>
                </span>
                <span class=\"showing-method-account-details gatewayWP\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayWP') { %>
                        style=\"display:none\"
                    <% } %>>
                    <% if (templateData.paymentMethods.wpRecurring.length == 1) { %>
                        <span><%- templateData.paymentMethods.wpRecurring[0]['cc_number'] %></span>
                    <% } else { %>
                        <span class=\"payment-source-tooltip gatewayWP \"><%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.wpRecurring.length}) %></span>
                    <% } %>
                </span>
                <span class=\"showing-method-account-details gatewayMB\"
                    <% if (templateData.firstAvailablePaymentMethod != 'gatewayMB') { %>
                        style=\"display:none\"
                    <% } %>>
                    <% if (templateData.paymentMethods.skrillPaymentVerified.length == 1) { %>
                        <%- T_('Order ID ') %> <%- templateData.paymentMethods.skrillPaymentVerified[0].onetap_invoice_id %>
                    <% } else { %>
                        <span class=\"payment-source-tooltip gatewayMB\"><%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.skrillPaymentVerified.length}) %></span>
                    <% } %>
                </span>
            </span>
            <span class=\"payment-source-preference is-preferred\"
                <% if (templateData.preferredPaymentMethod != templateData.firstAvailablePaymentMethod) { %>
                    style=\"display:none\"
                <% } %>>
                <%- T_('Preferred') %>
            </span>
            <span class=\"payment-method-info inline-block\">
                <% if (templateData.verifiedPaymentSourceCount > 1) { %>
                    <a href=\"#\" onclick=\"return false\" class=\" payment-source-edit-link open-change-preferred-method\">
                        <span class=\"fl-icon-pencil\"></span>
                        <%- T_('Edit preference') %>
                    </a>
                    <a href=\"#\" onclick=\"return false\" class=\"payment-source-cancel-link close-change-preferred-method\" style=\"display:none\">
                        <%- T_('Cancel') %>
                    </a>
                <% } %>
                <div class=\"clear\"></div>
                <div class=\"payment-source-edit\">
                    <select class=\"default-select change-preferred-method\" style=\"display:none\">
                        <option value=\"\" selected=\"selected\"><%- T_('Select your preferred payment method') %></option>
                        <% if (templateData.paymentMethods.ppEmail) { %>
                            <option value=\"gatewayPP\"><%- T_('PayPal:') %> (<%- templateData.paymentMethods.ppEmail %>)</option>
                        <% } %>
                        <% if (templateData.paymentMethods.gcRecurring) { %>
                            <option value=\"gatewayGC\"><%- T_('Credit Card:') %>
                                <% if (templateData.paymentMethods.gcRecurring.length == 1) { %>
                                    (<%- templateData.paymentMethods.gcRecurring[0]['cc_number'] %>)
                                <% } else { %>
                                    (<%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.gcRecurring.length}) %>)
                                <% } %>
                            </option>
                        <% } %>
                        <% if (templateData.paymentMethods.wpRecurring) { %>
                            <option value=\"gatewayWP\"><%- T_('Credit Card:') %>
                            <% if (templateData.paymentMethods.wpRecurring.length == 1) { %>
                                (<%- templateData.paymentMethods.wpRecurring[0]['cc_number'] %>)
                            <% } else { %>
                                (<%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.wpRecurring.length}) %>)
                            <% } %>
                            </option>
                        <% } %>
                        <% if (templateData.paymentMethods.skrillPaymentVerified) { %>
                            <option value=\"gatewayMB\"><%- T_('Skrill:') %>
                            <% if (templateData.paymentMethods.skrillPaymentVerified.length == 1) { %>
                                (<%- T_('Order ID:') %> <%- templateData.paymentMethods.skrillPaymentVerified[0].onetap_invoice_id %>)
                            <% } else { %>
                                (<%= T_('<[-numberOfAccounts]> accounts available', {'numberOfAccounts': templateData.paymentMethods.skrillPaymentVerified.length}) %>)
                            <% } %>
                            </option>
                        <% } %>
                    </select>
                </div>
            </span>
        </span>
        <div class=\"payments-source-generic-error alert alert-error\" style=\"display:none\"></div>
    </div>
<% } %>

</script>
